package basic4.codeup_basic100;

import java.util.Scanner;

//자바에 고투가 있었나...'-'
public class basic1071 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc = new Scanner(System.in);
		
		while(true) {

			int num = sc.nextInt();
				
			if(num == 0) {
				return;
			}
			
			System.out.println(num);
			
		}
		
		
	}

}
